var html=result,
header={"Referer":baseUrl};
headers={"headers":JSON.stringify(header)}
;
(function getImgList(){
    var p = "cp=\"(.*?)\"";
    var str = html.match(p)[1];
    host = "http://res.img.youzipi.net/"
    if (str!=null){
        var list = eval("("+java.base64Decode(str)+")").toString().split(",");
        var piclist = new Array([list.length]);
        for (var i = 0; i < list.length; i++) {
        piclist[i] = host + list[i];
        }
        return piclist;
    }else {
        return null;
    }
}()).map(uri=>'<img src="'+uri+','+JSON.stringify(header)+'">').join("\n")

