@js:
function getData(base, lastid, page, count) {
  let data = JSON.parse(java.aJax(base.replace("&comic_id=0", "&comic_id="+lastid)).match(/\{.*\}/)[0]).content_list;
  lastid = data[data.length-1].content_action
  count++;
  if (count<page)
    return getData(base, lastid, page, count);
  return data;
}
var jsonp = JSON.parse(result.match(/\{.*\}/)[0]);
if (baseUrl.match("searchinfo4web")) {
  jsonp.search_result_list;
}
if (baseUrl.match("timeline4web")) {
  var mark = parseInt(baseUrl.match(/#(\d+)/)[1]);
  var weekid = (jsonp.now_weekid+7-mark)%7;
  jsonp.timeline_list[weekid].comi_list;
}
if (baseUrl.match("allcomic4h5")) {
  var page = parseInt(baseUrl.match(/#(\d+)/)[1]);
  var list = jsonp.content_list;
  var lastid = list[list.length-1].content_action;
  if (page>1) list = getData(baseUrl,lastid,page,1);
  list
}

